require "sinatra"
require "sinatra/reloader"
require "http"
require "sinatra/cookies"

get("/") do
  if cookies.has_key?("counter") == false
    cookies.store("counter", 0)
  end

  erb(:restaurants_form)
end

post("/process_restaurants") do
  user_coors = coordinate(params.fetch("location"))
  user_lat = user_coors.fetch(:latitude)
  user_lng = user_coors.fetch(:longitude)
  @loc_weather = weather(user_lat, user_lng)

  current_search = JSON.generate(params)
  pp current_search
  user_distance = params.fetch("distance")
  user_cuisine = params.fetch("cuisine")
  user_budget = params.fetch("budget")
  user_rating = params.fetch("min_rating")

  if current_search == "{\"location\":\"Logan Square, Chicago, IL\",\"distance\":\"5\",\"cuisine\":\"French\",\"budget\":\"50\",\"min_rating\":\"3\"}"
    parsed_spoonacular_response = default_spoonacular_response
  else
    current_search_results = spoonacular_restaurants(user_lat, user_lng, user_distance, user_cuisine, user_budget, user_rating)
    parsed_spoonacular_response = JSON.parse(current_search_results)
  end

  if parsed_spoonacular_response.has_key?("restaurants")
    @restaurant_list = parsed_spoonacular_response.dig("restaurants")
  else
    @restaurant_list = nil
  end

  @default_results = default_spoonacular_response.dig("restaurants")

  erb(:restaurants_results)
end

post("/clear_cookies") do
  cookies.keys.each do |key|
    cookies.delete(key)
  end

  redirect :/
end

def coordinate(location)
  maps_url = "https://maps.googleapis.com/maps/api/geocode/json?address=#{location.gsub(" ", "+")}&key=#{ENV.fetch("GMAPS_KEY")}"
  raw_maps_response = HTTP.get(maps_url)
  parsed_maps_response = JSON.parse(raw_maps_response)

  current_loc = parsed_maps_response.dig("results", 0, "geometry", "location")
  current_lat = current_loc.fetch("lat")
  current_lng = current_loc.fetch("lng")

  return {
           latitude: current_lat,
           longitude: current_lng,
         }
end

def weather(latitude, longitude)
  pirate_weather_url = "https://api.pirateweather.net/forecast/#{ENV.fetch("PIRATE_WEATHER_KEY")}/#{latitude},#{longitude}"
  raw_pirate_weather_response = HTTP.get(pirate_weather_url)
  parsed_pirate_weather_response = JSON.parse(raw_pirate_weather_response)

  currently_hash = parsed_pirate_weather_response.fetch("currently")
  currently_temp_F = currently_hash.fetch("temperature").round(1)
  currently_summary = currently_hash.fetch("summary")
  currently_temp_C = ((currently_temp_F - 32) * 5 / 9).round(1)

  precip_threshold = 0.10
  hourly_hash = parsed_pirate_weather_response.fetch("hourly")
  next_twelve_hours = hourly_hash.fetch("data")[1..12]
  any_precipitation = false

  next_twelve_hours.each do |next_twelve_hours_hash|
    hour_precip_probability = next_twelve_hours_hash.fetch("precipProbability")
    if hour_precip_probability >= precip_threshold
      any_precipitation = true
    end
  end

  if any_precipitation
    current_loc_umbrella = "You might want to take an umbrella!"
  else
    current_loc_umbrella = "You probably won't need an umbrella."
  end

  return {
           umbrella: current_loc_umbrella,
           temp_F: currently_temp_F,
           temp_C: currently_temp_C,
           summary: currently_summary,
         }
end

def spoonacular_restaurants(lat, lng, distance, cuisine, budget, rating)
  spoonacular_url = "https://api.spoonacular.com/food/restaurants/search?apiKey=#{ENV.fetch("SPOONACULAR_API_KEY")}&lat=#{lat}&lng=#{lng}&distance=#{distance}&cuisine=#{cuisine}&budget=#{budget}&min-rating=#{rating}"
  return raw_spoonacular_response = HTTP.get(spoonacular_url)
end

